record.field:
# Bekleyen işlemleri kontrol et
pending_transactions = model.search([
    ('state', '=', 'pending'),
    ('pos_order_id', '!=', False),
    ('create_date', '<', datetime.datetime.now() - datetime.timedelta(hours=1))
])
for tx in pending_transactions:
    try:
        tx.provider_id.query_status(tx)
    except Exception as e:
        _logger.error("Error checking transaction %s: %s", tx.reference, e)

# Günlük mutabakat raporu oluştur from datetime import datetime, timedelta
yesterday = datetime.now() - timedelta(days=1) providers = env['payment.provider'].search([ ('code', 'in', ['akbank', 'garanti', 'isbank', 'ziraat', 'halkbank', 'vakifbank', 'vakifkatilim', 'yapikredi', 'finansbank', 'denizbank', 'teb', 'sekerbank', 'kuveytturk', 'param', 'tosla']), ('state', '=', 'enabled') ])
for provider in providers: reconciliation = env['pos.reconciliation'].create({ 'name': f"MUT/{yesterday.strftime('%Y%m%d')}/{provider.code.upper()}", 'date_start': yesterday.date(), 'date_end': yesterday.date(), 'provider_id': provider.id, }) reconciliation.action_load_transactions()
